package notionsBase;

/**
 * @author ycourteau
 *
 */
public class Concatener {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String premiereChaine = "C'est";
		System.out.println(premiereChaine);
		
		String deuxiemeChaine = " une chaine concatenee";
		System.out.println(deuxiemeChaine);
		
		String troisiemeChaine = premiereChaine + deuxiemeChaine;
		System.out.println(troisiemeChaine);

	}

}
