package notionsBase;

/**
 * @author ycourteau
 *
 */
public class PrePost {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i = 3;
		System.out.println("i = " + i);
		
		i++;
		System.out.println("i = " + i);
		
		++i;
		System.out.println("i = " + i);
		
		System.out.println("i = " + ++i);
		
		System.out.println("i = " + i++);
		
		System.out.println("i = " + i);

	}

}
