package notionsBase;

/**
 * @author ycourteau
 *
 */
public class Unaire {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int res = +1;
		System.out.println("res = " + res);
		
		res--;
		System.out.println("res = " + res);
		
		res++;
		System.out.println("res = " + res);
		
		res = -res;
		System.out.println("res = " + res);
		
		res = -res;
		System.out.println("res = " + res);
		
		boolean succes = false;
		System.out.println("succes = " + succes);
		System.out.println("succes = " + !succes);

	}

}
